import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput } from 'react-native';
import { Provider, useDispatch, useSelector } from 'react-redux'
import { StatusBar } from 'expo-status-bar';
import { getSudokuBoard} from '../store/actions/sudokuActions'


export const Home = () => {
  const dispatch = useDispatch()
  const boardGame = []
  const {sudokuBoard} = useSelector(state => state.sudokuReducers)
  const [getBoard, setBoard] = useState([])

  useEffect( () => {
    dispatch(getSudokuBoard())
  },[dispatch])

  useEffect( () => {
    setBoard(sudokuBoard.board)
  },[sudokuBoard])

  if(sudokuBoard.length === 0 ) {
    return <Text> please wait ..</Text>
  } else {
    console.log(sudokuBoard.board, 'sudokuBoard.board')
    for( let i = 0 ; i < sudokuBoard.board.length ; i++){
      for( let j = 0 ; j < sudokuBoard.board[i].length ; j++){
        boardGame.push(sudokuBoard.board[i][j])
        console.log(sudokuBoard.board[i][j], 'sudokuBoard[i][j]')
      }
      console.log(boardGame, 'boardGame')
    }
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text> ini home </Text>
      {getBoard ? 
        <div>
          { 
            boardGame.map((item, idx) => {
                if((idx + 1) % 9 == 0){
                  return (
                    <>
                      <br/>
                    </>
                  )
                } else {
                  return (
                    <>
                    </>
                  )
                }
              })
            }
        </div>
        :
        null
      }
      <StatusBar style="auto" />
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
