import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions, Button } from 'react-native';
import { onChange } from 'react-native-reanimated';

export const Finish = ({navigation}) => {
  const [username, setUsername] = useState("")
  const onChangeHandle = (e) => {
    const { value } = e.target
    setUsername(value)
  }

  return (
    <>
      <View style={styles.container}>
        <Text style={styles.title}> Welcome to SUDOKU!  </Text>
        <TextInput style={styles.inputName} placeholderTextColor="#aaa69d" placeholder="enter your name . . ."
          onChange= {(e)=> onChangeHandle(e)}
        />
        <Button color="#ff793f" title="LET'S PLAY" onPress={
          () => navigation.navigate("Game", {
            name: username
          })
        }/> 
      </View>
    </> 
  )
}

const widthWindows = Dimensions.get("window").width
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#40407a'
  },
  title: {
    color: '#f7f1e3',
    fontSize: 20,
    marginBottom: 20,
    borderBottomColor: "#ff793f",
    borderBottomWidth: 3,
  },
  inputName: {
    height: 40, 
    borderColor: '#aaa69d', 
    borderBottomWidth: 1, 
    color: '#d1ccc0', 
    fontSize: 20, 
    paddingLeft:20, 
    marginBottom:20,
    textAlign: "center",
  }
});
