export const getSudokuBoard = (difficulty = "easy") => {
  return (dispact) => {
    fetch(`https://sugoku.herokuapp.com/board?difficulty=${difficulty}`, {
      method: 'GET',
      headers: {
      'Content-Type': 'application/json'
      },
    })
      .then( resp => resp.json())
      .then( data => 
        dispact({
          type: 'SET_SUDOKU_BOARD',
          payload: {
            sudokuBoard: data
          }
        })
      )
  }
}


export const validate = (board) => {
  return (dispact) => {
    fetch('https://sugoku.herokuapp.com/validate', {
      method: "POST",
      body: JSON.stringify(board),
      headers: {
        'Content-Type': 'application/json'
        },
    })
      .then( resp => resp.json() )
      .then( data => dispact({
        type: 'VALIDATE_BOARD',
        payload: {
          result: data
        }
      })
    )
  }
}
