import { Provider, useDispatch, useSelector } from 'react-redux'
import { getSudokuBoard, validate} from '../store/actions/sudokuActions'
import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions, Button } from 'react-native';

export const Board = ({sudokuBoard, navigation}) =>{
  const dispatch = useDispatch()
  const [getBoard, setBoard] = useState([])
  // const [getBoardOrigin, setBoardOrigin] = useState([])
  const [getResult, setResult] = useState("")
  const { result} = useSelector(state => state.sudokuReducers)

  useEffect( () => {
    console.log("useEffect kedua")
    setBoard([...sudokuBoard])
    // setBoardOrigin(sudokuBoard)
  },[sudokuBoard])

  const handleOnChange = (e,row, col) => {
    const {value} = e.target
    console.log(value,'<<value')
    const newBoard = [...getBoard]
    newBoard[row][col] = +value
    console.log(newBoard,'<<newBoard')
    setBoard(newBoard)
  }

  if(getBoard.length>0 && sudokuBoard.length >0 ){
    console.log("getBoard", getBoard)
    console.log("sudokuBoard>>",sudokuBoard)
  }

  useEffect( () => {
    console.log("useEffect ketiga")
    if(result){
        navigation.navigate("Finish",{
          result: getResult
        })
    }
  },[result])

  const validateSudoku  = () =>{
    console.log(getBoard,"getBoard")
    dispatch(validate(getBoard))
  }

  return (
    <View>
      <View  style={styles.wrapperBoard}>
        { getBoard.map((board, row) => {
          return (
            <View key={`${row}`}>
              {board.map((item, col) => {
                  return (
                    <>
                    {sudokuBoard[row][col] == 0 ? 
                      <TextInput key={`${col}`} value="" 
                      onChange={(e) => handleOnChange(e,row, col)} style={styles.box}
                      maxLength={1}
                      /> 
                    : 
                      <TextInput key={`${col}`} value={`${item}`}
                      onChange={(e) => handleOnChange(e,row, col)} editable={false} style={styles.boxFix}
                      /> 
                    }

                    </>
                  )

                })

              }
              </View>
              )
            })  
          }      
      </View>
      <View style={styles.validateBtn}>
        <Button color="#706fd3" style={styles.validateBtn} onPress={ ()=>  {
          validateSudoku()
        }} title="Validate"/>
      </View>
    </View> 
  )
}

const widthWindows = Dimensions.get("window").width
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#40407a'
  },
  box: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    color: 'black',
    borderWidth: 1,
    borderColor:'#2c2c54'  
  },
  boxFix: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    borderWidth:1,
    borderColor:'#2c2c54',
    color: 'red'
  },
  buttonDiff: {
    marginLeft: 5,
    marginRight: 5
  },
  wrapper: {
    flexDirection: "row",
    marginBottom: 5,
    marginTop: 5
  },
  wrapperBoard: {
    flexDirection: "row",
    marginBottom: 5,
    marginTop: 5,
    backgroundColor: '#d1ccc0',
    padding: 5
  },
  title: {
    color: '#f7f1e3',
    fontSize: 20,
    marginBottom: 20,
    borderBottomColor: "#ff793f",
    borderBottomWidth: 3,
    textAlign: "center"
  },
  validateBtn:{
    marginTop: 10,
    borderTopColor: "#ff793f",
    borderTopWidth: 3,
  }
});
