import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput } from 'react-native';
import { Provider, useDispatch, useSelector } from 'react-redux'
import { StatusBar } from 'expo-status-bar';
import { getSudokuBoard} from '../store/actions/sudokuActions'


export const Home = () => {
  const dispatch = useDispatch()
  const boardGame = []
  const {sudokuBoard} = useSelector(state => state.sudokuReducers)
  const [getBoard, setBoard] = useState([])

  useEffect( () => {
    dispatch(getSudokuBoard())
  },[dispatch])

  useEffect( () => {
    console.log('sudokuBoard.board1', sudokuBoard.board)
    setBoard(sudokuBoard.board)
  },[sudokuBoard])

  console.log(sudokuBoard, 'sudokuBoard')
  if(sudokuBoard.length == 0 ) {
    return <Text> please wait ..</Text>
  } else {
    console.log(getBoard,'getBoard')
    for( let i = 0 ; i < sudokuBoard.length ; i++){
      for( let j = 0 ; j < sudokuBoard[i].length ; j++){
        boardGame.push(sudokuBoard[i][j])
      }
    }
    console.log(boardGame, 'board')
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text> ini home </Text>
      {getBoard ? 
        <div>
          { 
            getBoard.map((item, idx) => {
                return <TextInput key={idx} value={`${item}`} style= {{ width: 20}}/> 
              })
            }
        </div>
        :
        null
      }
      <StatusBar style="auto" />
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
