import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions, Button } from 'react-native';
import { useDispatch, useSelector } from 'react-redux'
import { StatusBar } from 'expo-status-bar';
import { getSudokuBoard, validate} from '../store/actions/sudokuActions'
import { Board } from '../components/Board'


export const Game = ({route: {params}}) => {
  const dispatch = useDispatch()
  const {sudokuBoard} = useSelector(state => state.sudokuReducers)
  const [getBoard, setBoard] = useState([])

  useEffect( () => {
    dispatch(getSudokuBoard(params.difficulty))
  },[dispatch])

  useEffect( () => {
    setBoard(JSON.parse(JSON.stringify(sudokuBoard)))
  },[sudokuBoard])


  if(getBoard.length === 0 ) {
    return <Text> please wait ..</Text>
  }


  const chooseDifficulty = (difficulty)  => {
    dispatch(getSudokuBoard(difficulty))
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}> Solve our SUDOKU, <strong style={{color:"#ffb142"}}> {params.name} </strong>! </Text>
      <View style={styles.wrapper}>
        <View style={styles.buttonDiff}>
          <Button color="#ffb142" onPress={() => chooseDifficulty("easy")} title="Easy"/>
        </View>
        <View style={styles.buttonDiff}>
          <Button color="#ff793f" onPress={() => chooseDifficulty("medium")} title="Medium"/>
        </View>
        <View style={styles.buttonDiff}>
          <Button color="#ff5252" onPress={() => chooseDifficulty("hard")} title="Hard"/>
        </View>
      </View>
      {getBoard.length >0 ? 
        <View>
          <Board getBoard={getBoard}/>
        </View>
        :
        null
      }
      <StatusBar style="auto" />
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  title: {
    color: '#f7f1e3',
    fontSize: 20,
    marginBottom: 20,
    borderBottomColor: "#ff793f",
    borderBottomWidth: 3,
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#40407a'
  },
  buttonDiff: {
    marginLeft: 5,
    marginRight: 5
  },
  wrapper: {
    flexDirection: "row",
    marginBottom: 10,
    marginTop: 5
  },
});
