import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions, Button } from 'react-native';
import { Provider, useDispatch, useSelector } from 'react-redux'
import { StatusBar } from 'expo-status-bar';
import { getSudokuBoard} from '../store/actions/sudokuActions'


export const Home = () => {
  const dispatch = useDispatch()
  const {sudokuBoard} = useSelector(state => state.sudokuReducers)
  const [getBoard, setBoard] = useState([])

  useEffect( () => {
    dispatch(getSudokuBoard())
  },[dispatch])

  useEffect( () => {
    setBoard(sudokuBoard)
  },[sudokuBoard])


  if(getBoard.length === 0 ) {
    return <Text> please wait ..</Text>
  }

  const handleOnChange = (e,row, col) => {
    const {value} = e.target
    const newBoard =getBoard
    newBoard.board[row][col] = +value
    setBoard(newBoard)
  }

  const chooseDifficulty = (difficulty)  => {
    console.log(difficulty)
    dispatch(getSudokuBoard(difficulty))
    // fetch(`https://sugoku.herokuapp.com/board?difficulty=${difficulty}`, {
    //   method: "GET",
    //   headers: {
    //     'Content-Type': 'application/json'
    //     },
    // })
    //   .then( resp => resp.json() )
    //   .then( data => setBoard(data))
  }

  const validateSudoku = () =>{
    console.log(getBoard,"getBoard")
    fetch('https://sugoku.herokuapp.com/validate', {
      method: "POST",
      body: JSON.stringify(getBoard),
      headers: {
        'Content-Type': 'application/json'
        },
    })
      .then( resp => resp.json() )
      .then( resp => alert(resp.status))
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text> ini home </Text>
      <Button onPress={()=>  chooseDifficulty("easy")} title="Easy"/>
      <Button onPress={()=>  chooseDifficulty("medium")} title="Medium"/>
      <Button onPress={()=>  chooseDifficulty("hard")} title="Hard"/>

      {getBoard.board? 
        <View style={{flexDirection: "row"}}>
          { 
          // !boardUpdate &&
          getBoard.board.map((board, row) => {
            return (
              <View>
                {board.map((item, col) => {
                    return (
                      <>
                      {item === 0 ? 
                        <TextInput key={`${col}`} defaultValue="" 
                        onChange={(e) => handleOnChange(e,row, col)} style={styles.box}
                        /> 
                      : 
                        <TextInput key={`${col}`} value={`${item}`} editable={false}
                        onChange={(e) => handleOnChange(e,row, col)} style={styles.boxFix}
                        /> 
                      }

                        {(col + 1) % 9 == 0 ? 
                          <br/>
                          :
                          null
                        }
                      </>
                    )

                  })

                }
                </View>
                )}
              )

            }
            <Button onPress={()=>  validateSudoku()} title="Validate"/>
        </View>
        :
        null
      }
      <StatusBar style="auto" />
    </ScrollView>
  )
}

const widthWindows = Dimensions.get("window").width
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  box: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    borderWidth:1,
    borderColor:'grey'  
  },
  boxFix: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    borderWidth:1,
    borderColor:'grey',
    color: 'red'
  }
});
