import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions, Button } from 'react-native';
import { Provider, useDispatch, useSelector } from 'react-redux'
import { StatusBar } from 'expo-status-bar';
import { getSudokuBoard, validate} from '../store/actions/sudokuActions'


export const Game = ({route: {params}, navigation}) => {
  const dispatch = useDispatch()
  const {sudokuBoard, result} = useSelector(state => state.sudokuReducers)
  const [getBoard, setBoard] = useState([])
  const [getResult, setResult] = useState("")

  useEffect( () => {
    dispatch(getSudokuBoard())
  },[dispatch])

  useEffect( () => {
    setBoard(sudokuBoard)
  },[sudokuBoard])

  useEffect( () => {
    if(result){
      setResult(result.status)
    }
  },[result])


  if(getBoard.length === 0 ) {
    return <Text> please wait ..</Text>
  }

  const handleOnChange = (e,row, col) => {
    const {value} = e.target
    const newBoard =getBoard
    newBoard.board[row][col] = +value
    setBoard(newBoard)
  }

  const chooseDifficulty = (difficulty)  => {
    console.log(difficulty)
    dispatch(getSudokuBoard(difficulty))
  }

  const validateSudoku = () =>{
    console.log(getBoard,"getBoard")
    dispatch(validate(getBoard))
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}> Solve our SUDOKU, <strong style={{color:"#ffb142"}}> {params.name} </strong>! </Text>
      <View style={styles.wrapper}>
        <View style={styles.buttonDiff}>
          <Button color="#ffb142" onPress={() => chooseDifficulty("easy")} title="Easy"/>
        </View>
        <View style={styles.buttonDiff}>
          <Button color="#ff793f" onPress={() => chooseDifficulty("medium")} title="Medium"/>
        </View>
        <View style={styles.buttonDiff}>
          <Button color="#ff5252" onPress={() => chooseDifficulty("hard")} title="Hard"/>
        </View>
      </View>

      {getBoard.board? 
        <View style={styles.wrapperBoard}>
          { 
          // !boardUpdate &&
          getBoard.board.map((board, row) => {
            return (
              <View key={`${row}`}>
                {board.map((item, col) => {
                    return (
                      <>
                      {item === 0 ? 
                        <TextInput key={`${col}`} defaultValue="" 
                        onChange={(e) => handleOnChange(e,row, col)} style={styles.box}
                        maxLength="1"
                        /> 
                      : 
                        <TextInput key={`${col}`} value={`${item}`} editable={false}
                        onChange={(e) => handleOnChange(e,row, col)} style={styles.boxFix}
                        /> 
                      }

                      </>
                    )

                  })

                }
                </View>
                )}
              )

            }
        </View>
        :
        null
      }
      <View style={styles.validateBtn}>
        <Button color="#706fd3" style={styles.validateBtn} onPress={()=>  {
          validateSudoku()
          navigation.navigate("Finish",{
            result: getResult
          })
        }} title="Validate"/>
      </View>
      <StatusBar style="auto" />
    </ScrollView>
  )
}

const widthWindows = Dimensions.get("window").width
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#40407a'
  },
  box: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    borderWidth:1,
    borderColor:'#2c2c54'  
  },
  boxFix: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    borderWidth:1,
    borderColor:'#2c2c54',
    color: '#b33939'
  },
  buttonDiff: {
    marginLeft: 5,
    marginRight: 5
  },
  wrapper: {
    flexDirection: "row",
    marginBottom: 5,
    marginTop: 5
  },
  wrapperBoard: {
    flexDirection: "row",
    marginBottom: 5,
    marginTop: 5,
    backgroundColor: '#d1ccc0',
    padding: 5
  },
  title: {
    color: '#f7f1e3',
    fontSize: 20,
    marginBottom: 20,
    borderBottomColor: "#ff793f",
    borderBottomWidth: 3,
    textAlign: "center"
  },
  validateBtn:{
    marginTop: 10,
    borderTopColor: "#ff793f",
    borderTopWidth: 3,
  }
});
