import React from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions, Button } from 'react-native';

const handleOnChange = (e,row, col) => {
  const {value} = e.target
  console.log(value,'<<value')
  const newBoard = [...getBoard]
  newBoard[row][col] = +value
  console.log(newBoard,'<<newBoard')
  setBoard(newBoard)
}

useEffect( () => {
  console.log("useEffect kedua")
  setBoard(sudokuBoard)
  setBoardOrigin(sudokuBoard)
},[sudokuBoard])

export const Board = ({sudokuBoard}) =>{
  return (
    <>
    { getBoard.map((board, row) => {
      return (
        <View key={`${row}`}>
          {board.map((item, col) => {
              return (
                <>
                {getBoardOrigin[row][col] == 0 ? 
                  <TextInput key={`${col}`} value="" 
                  onChange={(e) => handleOnChange(e,row, col)} style={styles.box}
                  maxLength={1}
                  /> 
                : 
                  <TextInput key={`${col}`} value={`${item}`}
                  onChange={(e) => handleOnChange(e,row, col)} editable={false} style={styles.boxFix}
                  /> 
                }

                </>
              )

            })

          }
          </View>
          )
        })  
      }      
    </> 
  )
}

const widthWindows = Dimensions.get("window").width
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#40407a'
  },
  box: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    color: 'black',
    borderWidth: 1,
    borderColor:'#2c2c54'  
  },
  boxFix: {
    textAlign:'center', 
    width: (widthWindows-30)/9, 
    height: 30, 
    borderWidth:1,
    borderColor:'#2c2c54',
    color: 'red'
    // color: '#b33939'
  },
  buttonDiff: {
    marginLeft: 5,
    marginRight: 5
  },
  wrapper: {
    flexDirection: "row",
    marginBottom: 5,
    marginTop: 5
  },
  wrapperBoard: {
    flexDirection: "row",
    marginBottom: 5,
    marginTop: 5,
    backgroundColor: '#d1ccc0',
    padding: 5
  },
  title: {
    color: '#f7f1e3',
    fontSize: 20,
    marginBottom: 20,
    borderBottomColor: "#ff793f",
    borderBottomWidth: 3,
    textAlign: "center"
  },
  validateBtn:{
    marginTop: 10,
    borderTopColor: "#ff793f",
    borderTopWidth: 3,
  }
});
