import React , { useEffect, useState } from 'react'
import { StyleSheet, Text, View, ScrollView, TextInput, Dimensions } from 'react-native';
import { Provider, useDispatch, useSelector } from 'react-redux'
import { StatusBar } from 'expo-status-bar';
import { getSudokuBoard} from '../store/actions/sudokuActions'


export const Home = () => {
  const dispatch = useDispatch()
  const {sudokuBoard} = useSelector(state => state.sudokuReducers)
  const [getBoard, setBoard] = useState([])
  const [boardUpdate, setBoardUpdate] = useState(false)
  const widthWindows = Dimensions.get("window").width

  useEffect( () => {
    dispatch(getSudokuBoard())
  },[dispatch])

  useEffect( () => {
    setBoard(sudokuBoard)
  },[sudokuBoard])

  if(getBoard.length === 0 ) {
    return <Text> please wait ..</Text>
  }

  // console.log(getBoard,'initial')
  const handleOnChange = (e,row, col) => {
    setBoardUpdate(true)
    const {value} = e.target
    console.log(value, 'value')
    console.log(row, 'row')
    console.log(col, 'col')
    const newBoard =getBoard.board
    newBoard[row][col] = +value
    setBoard(newBoard)
    console.log(getBoard,'akhir')
    setBoardUpdate(false)
  }

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text> ini home </Text>
      {getBoard.board? 
        <div>
          { 
          !boardUpdate &&
          getBoard.board.map((board, row) => {
              return board.map((item, col) => {
                  return (
                    <>
                    <TextInput key={col} value={
                        (item=="0") ? "" : `${item}`
                      } 
                      onChange={(e) => handleOnChange(e,row, col)} style= {{textAlign:'center', width: (widthWindows-30)/9, height: 30, borderWidth:1 ,borderColor:'grey'  }}/> 
                      {(col + 1) % 9 == 0 ? 
                        <br/>
                        :
                        null
                      }
                    </>
                  )

                })
              })
            }
        </div>
        :
        null
      }
      <StatusBar style="auto" />
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
